import React, { useState, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import {
  Container,
  Box,
  Typography,
  Paper,
  Button,
  TextField,
  CircularProgress,
  Alert,
  Snackbar,
  LinearProgress,
  Stepper,
  Step,
  StepLabel
} from '@mui/material';
import axios from 'axios';
import './App.css';

function App() {
  const [file, setFile] = useState<File | null>(null);
  const [title, setTitle] = useState('');
  const [isConverting, setIsConverting] = useState(false);
  const [isTraining, setIsTraining] = useState(false);
  const [isTesting, setIsTesting] = useState(false);
  const [isPublishing, setIsPublishing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState<string | null>(null);
  const [trainingProgress, setTrainingProgress] = useState(0);
  const [estimatedTime, setEstimatedTime] = useState(0);
  const [currentStep, setCurrentStep] = useState('');
  const [statusCheckInterval, setStatusCheckInterval] = useState<NodeJS.Timeout | null>(null);
  const [trainingMetrics, setTrainingMetrics] = useState<{
    accuracy: number | null;
    precision: number | null;
    recall: number | null;
  }>({
    accuracy: null,
    precision: null,
    recall: null
  });
  const [testingMetrics, setTestingMetrics] = useState<{
    accuracy: number | null;
    precision: number | null;
    recall: number | null;
  }>({
    accuracy: null,
    precision: null,
    recall: null
  });
  const [activeStep, setActiveStep] = useState(0);

  const steps = ['Convert', 'Train', 'Test', 'Publish'];

  const onDrop = useCallback((acceptedFiles: File[]) => {
    if (acceptedFiles.length > 0) {
      setFile(acceptedFiles[0]);
      setActiveStep(0);
    }
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet': ['.xlsx'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/pdf': ['.pdf']
    },
    multiple: false
  });

  const checkStatus = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/training-status');
      const { 
        progress, 
        estimated_time, 
        current_step, 
        error, 
        is_training,
        is_testing,
        training_metrics,
        testing_metrics
      } = response.data;
      
      setTrainingProgress(progress);
      setEstimatedTime(estimated_time);
      setCurrentStep(current_step);
      setIsTraining(is_training);
      setIsTesting(is_testing);
      setTrainingMetrics(training_metrics);
      setTestingMetrics(testing_metrics);

      if (error) {
        setError(error);
        setIsConverting(false);
        setIsTraining(false);
        setIsTesting(false);
        if (statusCheckInterval) {
          clearInterval(statusCheckInterval);
        }
      }

      if (progress === 100) {
        if (is_training) {
          setIsTraining(false);
          setActiveStep(2); // Move to Test step
        } else if (is_testing) {
          setIsTesting(false);
          setActiveStep(3); // Move to Publish step
        }
      }
    } catch (err) {
      setError('Failed to check status');
      setIsConverting(false);
      setIsTraining(false);
      setIsTesting(false);
      if (statusCheckInterval) {
        clearInterval(statusCheckInterval);
      }
    }
  };

  const handleConvert = async () => {
    if (!file) {
      setError('Please select a file');
      return;
    }

    setIsConverting(true);
    setError(null);
    setSuccess(null);
    setTrainingProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('action', 'convert');

      await axios.post('http://localhost:5000/api/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setIsConverting(false);
      setActiveStep(1); // Move to Train step
    } catch (err) {
      setError('Failed to convert file');
      setIsConverting(false);
    }
  };

  const handleTrain = async () => {
    if (!file) {
      setError('Please select a file');
      return;
    }

    setIsTraining(true);
    setError(null);
    setSuccess(null);
    setTrainingProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('action', 'train');

      await axios.post('http://localhost:5000/api/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      const interval = setInterval(checkStatus, 1000);
      setStatusCheckInterval(interval);
    } catch (err) {
      setError('Failed to start training');
      setIsTraining(false);
    }
  };

  const handleTest = async () => {
    if (!file) {
      setError('Please select a file');
      return;
    }

    setIsTesting(true);
    setError(null);
    setSuccess(null);
    setTrainingProgress(0);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('action', 'test');

      await axios.post('http://localhost:5000/api/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      const interval = setInterval(checkStatus, 1000);
      setStatusCheckInterval(interval);
    } catch (err) {
      setError('Failed to start testing');
      setIsTesting(false);
    }
  };

  const handlePublish = async () => {
    if (!file || !title) {
      setError('Please select a file and enter a title');
      return;
    }

    setIsPublishing(true);
    setError(null);
    setSuccess(null);

    try {
      const formData = new FormData();
      formData.append('file', file);
      formData.append('title', title);
      formData.append('action', 'publish');

      const response = await axios.post('http://localhost:5000/api/process', formData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      setSuccess('File published successfully!');
      setIsPublishing(false);
    } catch (err) {
      setError('Failed to publish');
      setIsPublishing(false);
    }
  };

  const handleCloseSnackbar = () => {
    setError(null);
    setSuccess(null);
  };

  return (
    <Container maxWidth="md">
      <Box sx={{ my: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          File Converter
        </Typography>

        <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
          {steps.map((label) => (
            <Step key={label}>
              <StepLabel>{label}</StepLabel>
            </Step>
          ))}
        </Stepper>

        <Paper
          {...getRootProps()}
          sx={{
            p: 3,
            textAlign: 'center',
            cursor: 'pointer',
            backgroundColor: isDragActive ? '#f0f0f0' : 'white',
            border: '2px dashed #ccc',
            mb: 2
          }}
        >
          <input {...getInputProps()} />
          {isDragActive ? (
            <Typography>Drop the file here...</Typography>
          ) : (
            <Typography>
              Drag and drop a file here, or click to select a file
            </Typography>
          )}
        </Paper>

        {file && (
          <Box sx={{ mb: 2 }}>
            <Typography>Selected file: {file.name}</Typography>
          </Box>
        )}

        <TextField
          fullWidth
          label="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          sx={{ mb: 2 }}
        />

        <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
          <Button
            variant="contained"
            color="primary"
            onClick={handleConvert}
            disabled={!file || isConverting || activeStep > 0}
          >
            {isConverting ? (
              <>
                <CircularProgress size={24} sx={{ mr: 1 }} />
                Converting...
              </>
            ) : (
              'Convert'
            )}
          </Button>

          <Button
            variant="contained"
            color="primary"
            onClick={handleTrain}
            disabled={!file || isTraining || activeStep !== 1}
          >
            {isTraining ? (
              <>
                <CircularProgress size={24} sx={{ mr: 1 }} />
                Training...
              </>
            ) : (
              'Train'
            )}
          </Button>

          <Button
            variant="contained"
            color="primary"
            onClick={handleTest}
            disabled={!file || isTesting || activeStep !== 2}
          >
            {isTesting ? (
              <>
                <CircularProgress size={24} sx={{ mr: 1 }} />
                Testing...
              </>
            ) : (
              'Test'
            )}
          </Button>

          <Button
            variant="contained"
            color="primary"
            onClick={handlePublish}
            disabled={!file || !title || isPublishing || activeStep !== 3}
          >
            {isPublishing ? (
              <>
                <CircularProgress size={24} sx={{ mr: 1 }} />
                Publishing...
              </>
            ) : (
              'Publish'
            )}
          </Button>
        </Box>

        {(isConverting || isTraining || isTesting) && (
          <Box sx={{ width: '100%', mb: 2 }}>
            <Typography variant="body2" gutterBottom>
              {currentStep} - {trainingProgress}% complete
            </Typography>
            <Typography variant="body2" color="text.secondary" gutterBottom>
              Estimated time remaining: {estimatedTime} seconds
            </Typography>
            <LinearProgress variant="determinate" value={trainingProgress} />
          </Box>
        )}

        {trainingMetrics.accuracy !== null && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              Training Metrics:
            </Typography>
            <Typography variant="body2">
              Accuracy: {(trainingMetrics.accuracy * 100).toFixed(1)}%
            </Typography>
            {trainingMetrics.precision !== null && (
              <Typography variant="body2">
                Precision: {(trainingMetrics.precision * 100).toFixed(1)}%
              </Typography>
            )}
            {trainingMetrics.recall !== null && (
              <Typography variant="body2">
                Recall: {(trainingMetrics.recall * 100).toFixed(1)}%
              </Typography>
            )}
          </Box>
        )}

        {testingMetrics.accuracy !== null && (
          <Box sx={{ mt: 2 }}>
            <Typography variant="subtitle2" gutterBottom>
              Testing Metrics:
            </Typography>
            <Typography variant="body2">
              Accuracy: {(testingMetrics.accuracy * 100).toFixed(1)}%
            </Typography>
            {testingMetrics.precision !== null && (
              <Typography variant="body2">
                Precision: {(testingMetrics.precision * 100).toFixed(1)}%
              </Typography>
            )}
            {testingMetrics.recall !== null && (
              <Typography variant="body2">
                Recall: {(testingMetrics.recall * 100).toFixed(1)}%
              </Typography>
            )}
          </Box>
        )}

        <Snackbar
          open={!!error || !!success}
          autoHideDuration={6000}
          onClose={handleCloseSnackbar}
        >
          <Alert
            onClose={handleCloseSnackbar}
            severity={error ? 'error' : 'success'}
            sx={{ width: '100%' }}
          >
            {error || success}
          </Alert>
        </Snackbar>
      </Box>
    </Container>
  );
}

export default App;
